"use strict";var KTLayoutToolbar=function(){var toolbar;var initForm=function(){var rangeSlider=document.querySelector("#kt_toolbar_slider");var rangeSliderValueElement=document.querySelector("#kt_toolbar_slider_value");if(!rangeSlider){return}noUiSlider.create(rangeSlider,{start:[5],connect:[true,false],step:1,format:wNumb({decimals:1}),range:{min:[1],max:[10]}});rangeSlider.noUiSlider.on("update",function(values,handle){rangeSliderValueElement.innerHTML=values[handle]});var handle=rangeSlider.querySelector(".noUi-handle");handle.setAttribute("tabindex",0);handle.addEventListener("click",function(){this.focus()});handle.addEventListener("keydown",function(event){var value=Number(rangeSlider.noUiSlider.get());switch(event.which){case 37:rangeSlider.noUiSlider.set(value-1);break;case 39:rangeSlider.noUiSlider.set(value+1);break}})};return{init:function(){toolbar=document.querySelector("#kt_toolbar");if(!toolbar){return}initForm()}}}();KTUtil.onDOMContentLoaded(function(){KTLayoutToolbar.init()});